import React, {Component} from 'react';
import "./_basket.scss"
import Header from "../../general_components/header/Header";
import Footer from "../../general_components/footer/Footer";
import BasketNav from "./basket_nav/BasketNav";
import BasketStep1 from "./basket_step_1/BasketStep1";

class Basket extends Component
{

    state =
        {
            step1: {},
            step2: {},
            step3: {},
            currentStep: 1,
        };


    stepUpdate = (stepValue) =>
    {

        switch (this.state.currentStep) {
            case 1:
                this.setState({
                    step1: stepValue,
                    currentStep: this.state.currentStep + 1
                });
                break;
            case 2:
                this.setState({
                    step2: stepValue,
                    currentStep: this.state.currentStep + 1
                });
                break;
            case 3:
                this.setState({
                    step3: stepValue,
                    currentStep: this.state.currentStep + 1
                });
                break;
        }








        if(this.state.currentStep === 1)
        {
            this.setState({
                step1: stepValue,
                currentStep: this.state.currentStep + 1
            })
        }

        if(this.state.currentStep === 2)
        {
            this.setState({
                step2: stepValue,
                currentStep: this.state.currentStep + 1
            })
        }

        if(this.state.currentStep === 3)
        {
            this.setState({
                step3: stepValue,
                currentStep: this.state.currentStep + 1
            })
        }

        if(this.state.currentStep === 4)
        {
            this.setState({
                step4: stepValue,
                currentStep: this.state.currentStep + 1,
            })
        }

    };

    goBack = () =>
    {
        this.setState({
            currentStep: this.state.currentStep - 1,
        })
    };

    render() {
        const {basketStep, login, setBasketStep, basketPath, basket, basketSum, setClearLogin, basketAmount} = this.props;
        return (
            <>
                <Header login={login}
                        setClearLogin={setClearLogin}
                        basketPath={basketPath}
                        basketSum={basketSum}
                        basketAmount={basketAmount}/>
                <BasketNav basketStep={basketStep}/>
                {basketStep === 1 &&
                <BasketStep1 setStep={setBasketStep}
                             basket={basket}
                             basketSum={basketSum}/>}
                <Footer/>
            </>
        )
    }
}

export default Basket;
