import React, {Component} from 'react';
import "./_product.scss"

class Products extends Component
{
    state = {
        products: [],
        currentPage: 1,
        infoPerPage: 6,

        basketList: [],
        pageNumbers: [],

        numberOfPages: Math.ceil(products.length/infoPerPage)
    };

    componentDidMount() {

         const url = "http://localhost:3000/products";

            fetch(url)
            .then(response => {
                return response.json()
            })
            .then(products =>
            {
                this.setState({
                    products: products
                });

                const {currentPage, infoPerPage, pageNumbers} = this.state;

                const numberOfPages = Math.ceil(this.state.products.length/this.state.infoPerPage);

                for (let i = 1; i <= numberOfPages ; i++)
                {
                    const pageNumberElement =
                        <li key={"product-content-page" + i}
                            onClick={e => this.handleClick(e, i)}
                            className={(currentPage === i) ? "active" : ""}>
                            {i}
                        </li>;
                    pageNumbers.push(pageNumberElement);
                }

            })
            .catch(function(error) {
                console.log(error);
            });





    };


    handleClick = (e, i) =>
    {
        this.setState({
            currentPage: i
        })
    };

    basketAdd = (productAdded) =>
    {
        let newBasketList = [...this.state.basketList];
        newBasketList.push(productAdded);

        this.setState({
            basketList: newBasketList
        });

        console.log(newBasketList);
    };




    render() {
        const {products, currentPage, infoPerPage, pageNumbers, numberOfPages} = this.state;
        const indexOfLast = currentPage * infoPerPage;
        const indexOfFirst = indexOfLast - infoPerPage;
        const currentProducts = products.slice(indexOfFirst, indexOfLast);
        return (
            <>
                <section className="products">
                    <div className="container">
                        <div className="products-content">
                            <ul className="products-content-products">
                                {currentProducts.map(element =>
                                    <li key={element.id}>
                                        <div className="product">
                                            <figure className="product-img">
                                                <img src={"./assets/products/" + element.id + ".jpeg"}
                                                     alt={"product_" + element.id}/>
                                                <figcaption className="product-name">
                                                    product {element.id}
                                                </figcaption>
                                            </figure>
                                            <section className="product-info">
                                                <div className="product-info-price">
                                                    <i className="fas fa-2x fa-tag label"/>
                                                    <h3>{element.price / 100} z≈Ç</h3>
                                                </div>
                                                <div className="product-info-add">
                                                    <i className="fas fa-2x fa-plus-square"
                                                       onClick={this.basketAdd(element)}/>
                                                </div>
                                            </section>
                                        </div>
                                    </li>
                                )}
                                {(currentProducts.length % 3 !== 0) &&
                                (currentProducts.length % 3 === 2) ?
                                <li className="invisible-product"/>:
                                <>
                                    <li className="invisible-product"/>
                                    <li className="invisible-product"/>
                                </>}
                            </ul>
                            <ul className="products-content-pages">
                            {numberOfPages !== 1 &&
                                <>
                                    {pageNumbers}
                                </>}
                            </ul>
                        </div>
                    </div>
                </section>
            </>
        )
    }
}

export default Products;
