import React, {Component} from 'react';
import "./_basket.scss"
import Header from "../../general_components/header/Header";
import Footer from "../../general_components/footer/Footer";
import BasketNav from "./basket_nav/BasketNav";
import BasketStep1 from "./basket_step_1/BasketStep1";

class Basket extends Component
{
    state =
        {
            step: ""
        };

    setStep = (currentStep) =>
    {
        this.setState({
            step: currentStep
        });
        console.log(currentStep);
    };


    render() {
        const {step} = this.state;
        return (
            <>
                <Header login={this.props.login} setClearLogin={this.setClearLogin}/>
                <BasketNav/>
                {step === 1 &&
                <BasketStep1 setStep={this.setStep}/>}
                <Footer/>
            </>
        )
    }
}

export default Basket;
