import React, {Component} from 'react';
import "./../_lar.scss"
import "./_login.scss"
import Logo from "../../general_components/header/logo/Logo";
import Footer from "../../general_components/footer/Footer";
import {Link} from "react-router-dom";

class Login extends Component
{

    state =
        {
            users: "",

            login: "",
            loginExist: false,
            password: "",
            passwordVal: false,

            submitHandle: false,
        };

    componentDidMount()
    {
        const url = "http://localhost:3012/users";

        fetch(url)
        .then(response => {
            return response.json()
        })
        .then(users =>
        {
            this.setState({
                users: users
            });
        })
        .catch(function(error) {
            console.log(error);
        });
    };

    handleChangePassword = e =>
    {
        this.setState({
            password: e.target.value.replace(" ", "")
        });
        if(this.state.users.login === this.state.login)
        {
            this.setState({
                passwordVal: true,
            })
        }
        else
        {
            this.setState({
                passwordVal: false,
            })
        }
    };

    handleChangeLogin = e =>
    {
        this.setState({
            password: e.target.value.replace(" ", "")
        });

        for (let i = 0; i < this.state.users.length; i++)
        {
            this.setState({
                loginExist: false,
            });

            if(this.state.users[i].login === e.target.value)
            {
                this.setState({
                    loginExist: true,

                });
                break;
            }
        }
    };



    render() {
        return (
            <>
                <Logo/>
                <section className="login">
                    <div className="container lar-cont">
                        <div className="login-content">
                            <form>
                                <legend>
                                    Logowanie
                                </legend>
                                <div className="login-content-form-img">
                                    <i className="fas fa-5x fa-user-alt"/>
                                </div>
                                <label>Login:
                                    <input type="text"
                                           placeholder="Login"
                                           name="login"
                                           value={login}
                                           onChange={this.handleChangeLogin}/>
                                </label>
                                <label>Hasło:
                                    <input type="password"
                                           placeholder="Password"
                                           name="password"
                                           value={password}
                                           onChange={this.handleChangePassword}/>
                                </label>
                                <div className="lar-content-form-btns">
                                    <Link to="/registration">do rejestracji</Link>
                                    <input type="submit" value="zaloguj"/>
                                </div>
                            </form>
                        </div>
                    </div>
                </section>
                <Footer/>
            </>
        )
    }
}

export default Login;
