import React, {Component} from 'react';
import "./_basket_step_2.scss"

class BasketStep2 extends Component
{
    state =
        {
            disabled: true,

            users: "",

            name: "",
            nameVal: false,
            surname: "",
            surnameVal: false,
            email: "",
            emailVal: false,

            address: "",
            addressVal: false,
            zipCode: "",
            zipCodeVal: false,
            city: "",
            cityVal: false,
            phone: "",
            phoneVal: false,

            agreementChecked: false,

            submitHandle: false,
            submitMessage: ""
        };

    componentDidMount()
    {
        const url = "http://localhost:3012/users";

        fetch(url)
        .then(response => {
            return response.json()
        })
        .then(users =>
        {
            this.setState({
                users: users
            });
        })
        .catch(function(error) {
            console.log(error);
        });
    };

    validation = (e, valType, length) =>
    {
        e.target.value.length >= length ? valType=true : valType=false;

        this.setState({
            valType: valType,
        })
    };

    handleChangeName = e =>
    {
        this.setState({
            name: e.target.value
        });

        let nameVal;
        this.validation(e, nameVal, 2);
    };

    handleChangeSurname = e =>
    {
        this.setState({
            surname: e.target.value
        });
        let surnameVal;
        this.validation(e, surnameVal, 2);
    };

    handleChangeAddress = e =>
    {
        this.setState({
            address: e.target.value
        });
        let addressVal;
        this.validation(e, addressVal, 4);
    };

    handleChangeCity = e =>
    {
        this.setState({
            city: e.target.value
        });
        let cityVal;
        this.validation(e, cityVal, 2);
    };

    handleChangeZipCode = e =>
    {
        this.setState({
            zipCode: e.target.value
        });

        let zipCodeVal;
        e.target.value.toString().length = 6 ? zipCodeVal=true : zipCodeVal=false;

        this.setState({
            zipCodeVal: zipCodeVal,
        })
    };

    handleChangePhone = e =>
    {
        this.setState({
            phone: e.target.value
        });
        let phoneVal;
        this.validation(e, phoneVal, 9);
    };

    emailValidate = (email) =>
    {
        const emailValidationCharSet = /^(([^<>()[\]\\.,;:\s@]+(\.[^<>()[\]\\.,;:\s@]+)*)|(.+))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return emailValidationCharSet.test(email);
    };

    handleChangeEmail = e =>
    {
        this.setState({
            email: e.target.value
        });

        for (let i = 0; i < this.state.users.length; i++)
        {
            this.setState({
                emailAvailable: true,
            });

            if(this.state.users[i].email === e.target.value)
            {
                this.setState({
                    emailAvailable: false,
                });
                break;
            }
        }

        if(this.emailValidate(e.target.value))
        {
            this.setState({
                emailVal: true,
            })
        }
        else
        {
            this.setState({
                emailVal: false,
            })
        }
    };


    handleChangeAgreement = (e) =>
    {
        if(document.getElementById("agreement").checked === true)
        {
            this.setState({
                agreementChecked: true
            });
        }
        else
        {
            this.setState({
                agreementChecked: false
            });
        }

    };



    handleSubmit = () =>
    {
        const {setBasketStep, basketStep} = this.props;
        setBasketStep(basketStep + 1);
    };

    handleGoBack = () =>
    {
        const {setBasketStep, basketStep} = this.props;
        setBasketStep(basketStep - 1)
    };

    render() {

        const {disabled, email, emailVal, name, nameVal, surname, surnameVal, address, addressVal,
            city, cityVal, zipCode, zipCodeVal, phone, phoneVal, submitHandle, loginAvailable, emailAvailable, agreementChecked, submitMessage} = this.state;
        return (
            <>
                <section className="basket-step-2">
                    <div className="container basket-container">
                        <form>
                            <legend>Dane do wysyłki</legend>
                            <section className="basket-step-2-form-section">
                                <label>Imię:
                                    <span className="big-x2">*</span>
                                    <input type="text"
                                           placeholder="Name"
                                           name="name"
                                           value={name}
                                           onChange={this.handleChangeName}/>
                                </label>
                                <label>Nazwisko:
                                    <span className="big-x2">*</span>
                                    <input type="text"
                                           placeholder="Surname"
                                           name="surname"
                                           value={surname}
                                           onChange={this.handleChangeSurname}/>
                                </label>
                                <label className="obligatory">E-mail:
                                    <span className="big-x2">*</span>
                                    <input type="text"
                                           placeholder="E-mail"
                                           name="email"
                                           value={email}
                                           onChange={this.handleChangeEmail}/>
                                </label>
                                <label>Adres zamieszkania:
                                    <span className="big-x2">*</span>
                                    <input type="text"
                                           placeholder="Address"
                                           name="address"
                                           value={address}
                                           onChange={this.handleChangeAddress}
                                    />
                                </label>
                                <label>Miasto:
                                    <span className="big-x2">*</span>
                                    <input type="text"
                                           placeholder="City"
                                           name="city"
                                           value={city}
                                           onChange={this.handleChangeCity}
                                    />
                                </label>
                                <label>Kod pocztowy:
                                    <span className="big-x2">*</span>
                                    <input type="text"
                                           placeholder="Zip code"
                                           name="zipCode"
                                           value={zipCode}
                                           onChange={this.handleChangeZipCode}
                                    />
                                </label>
                                <label>Telefon:
                                    <span className="big-x2">*</span>
                                    <input type="text"
                                           placeholder="Phone"
                                           name="phone"
                                           value={phone}
                                           onChange={this.handleChangePhone}
                                    />
                                </label>
                            </section>
                            <section className="basket-step-2-form-section">
                                <div className="basket-step-2-form-section-agreements">
                                    <input type="checkbox"
                                           id="agreement"
                                           onChange={this.handleChangeAgreement}/>
                                    <label htmlFor="agreement">
                                        <p>*</p>
                                        <span/>
                                        <p>
                                            Akceptuję regulamin sklepu
                                        </p>
                                    </label>
                                </div>
                                {/*{(!agreementChecked && submitHandle) ?*/}
                                {/*    <div className="form-error">*/}
                                {/*        Zgoda jest obowiązkowa*/}
                                {/*    </div>:*/}
                                {/*    <div className="form-error-invisible"/>}*/}
                                <p className="basket-step-2-form-section-info">
                                    Pola oznaczone&nbsp;
                                    <span className="big-x1">*</span>
                                    &nbsp;są obowiązkowe
                                </p>
                            </section>
                        </form>
                        <section className="basket-step-btns">
                            {/*{!currentDelivery && <p>Aby przejść dalej wybierz opcje dostawy</p>}*/}
                            <button onClick={this.handleGoBack}>
                                Wstecz
                            </button>
                            <button onClick={this.handleSubmit} disabled={disabled}>
                                Dalej
                            </button>
                        </section>
                    </div>
                </section>
            </>
        )
    }
}

export default BasketStep2;
