import React, {Component} from 'react';
import "./../_lar.scss"
import "./_registration.scss"
import Logo from "../../general_components/header/logo/Logo";
import Footer from "../../general_components/footer/Footer";
import {Link} from "react-router-dom";

class Registration extends Component
{

    state =
        {
            users: "",

            name: "",
            surname: "",
            email: "",
            emailVal: false,

            login: "",
            loginVal: false,
            loginAvailable: false,
            password: "",
            passwordVal: false,
            passwordConfirm: "",
            passwordConfirmVal: false,

            address: "",
            zipCode: "",
            city: "",
            phone: "",

            submitHandle: false,
        };

    componentDidMount() {

        const url = "http://localhost:3012/users";

        fetch(url)
        .then(response => {
            return response.json()
        })
        .then(users =>
        {
            this.setState({
                users: users
            });
        })
        .catch(function(error) {
            console.log(error);
        });
    };

    handleChangeName = e =>
    {
        this.setState({
            name: e.target.value
        });
    };

    handleChangeSurname = e =>
    {
        this.setState({
            surname: e.target.value
        });
    };


    emailValidate = (email) =>
    {
        const emailValidationCharSet = /^(([^<>()[\]\\.,;:\s@]+(\.[^<>()[\]\\.,;:\s@]+)*)|(.+))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return emailValidationCharSet.test(email);
    };

    handleChangeEmail = e =>
    {
        this.setState({
            email: e.target.value
        });
        if(this.emailValidate(e.target.value))
        {
            this.setState({
                emailVal: true,
            })
        }
        else
        {
            this.setState({
                emailVal: false,
            })
        }
    };

    handleChangeLogin = e =>
    {
        this.setState({
            login: e.target.value.replace(" ", "")
        });

        for (let i = 0; i < this.state.users.length; i++)
        {
            this.setState({
                loginAvailable: true,
            });

            if(this.state.users[i].login === e.target.value)
            {
                this.setState({
                    loginAvailable: false,
                });
                break;
            }
        }

        if(e.target.value.length >= 3)
        {
            this.setState({
                loginVal: true,
            })
        }
        else
        {
            this.setState({
                loginVal: false,
            })
        }
    };

    handleChangePassword = e =>
    {
        this.setState({
            password: e.target.value.replace(" ", "")
        });
        if(e.target.value.length >= 5)
        {
            this.setState({
                passwordVal: true,
            })
        }
        else
        {
            this.setState({
                passwordVal: false,
            })
        }
    };

    handleChangePasswordConfirm = e =>
    {
        this.setState({
            passwordConfirm: e.target.value.replace(" ", "")
        });
        if(this.state.password === e.target.value)
        {
            this.setState({
                passwordConfirmVal: true,
            })
        }
        else
        {
            this.setState({
                passwordConfirmVal: false,
            })
        }
    };



    handleSubmit = e =>
    {
        e.preventDefault();

        console.log("submit");

        console.log(this.state.loginAvailable);
        console.log("?");
        console.log(!this.stateloginAvailable && this.state.loginVal);

        const {name, surname,
            email, emailVal, login, loginVal, password, passwordVal, loginAvailable, passwordConfirmVal,
            address, city, zipCode, phone} = this.state;

        if(emailVal && loginVal && passwordVal && passwordConfirmVal && loginAvailable)
        {
            console.log(loginAvailable);

            const url = "http://localhost:3012/users";

            fetch(url,
                {
                    headers: {"Content-Type": "application/json"},
                    method: 'POST',
                    dataType: "json",
                    body: JSON.stringify({name, surname, email, login, password, address, city, zipCode, phone}),
                })
            .then(resp =>{
                if (!resp.ok) {
                    throw new Error("something is wrong...");
                }
                else
                {
                    this.setState({
                        submitMessage: "Dziękujemy za rejestracje",
                        name: "",
                        surname: "",
                        email: "",
                        emailVal: false,

                        login: "",
