import React, {Component} from 'react';
import "./../_lar.scss"
import "./_registration.scss"
import Logo from "../../general_components/header/logo/Logo";
import Footer from "../../general_components/footer/Footer";
import {Link} from "react-router-dom";

class Registration extends Component
{

    state =
        {
            name: "",
            surname: "",
            email: "",
            emailVal: false,

            login: "",
            loginVal: false,
            password: "",
            passwordVal: false,
            passwordConfirm: "",
            passwordConfirmVal: false,

            address: "",
            zipCode: "",
            city: "",
            phone: "",

            submitHandle: false,
        };

    handleChangeName = e =>
    {
        this.setState({
            name: e.target.value
        });
    };

    handleChangeSurname = e =>
    {
        this.setState({
            surname: e.target.value
        });
    };


    emailValidate = (email) =>
    {
        const emailValidationCharSet = /^(([^<>()[\]\\.,;:\s@]+(\.[^<>()[\]\\.,;:\s@]+)*)|(.+))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return emailValidationCharSet.test(email);
    };

    handleChangeEmail = e =>
    {
        this.setState({
            email: e.target.value
        });
        if(this.emailValidate(e.target.value))
        {
            this.setState({
                emailVal: true,
            })
        }
        else
        {
            this.setState({
                emailVal: false,
            })
        }
    };

    handleChangeLogin = e =>
    {
        this.setState({
            login: e.target.value.replace(" ", "")
        });
        if(this.state.login.length >= 3)
        {
            this.setState({
                loginVal: true,
            })
        }
        else
        {
            this.setState({
                loginVal: false,
            })
        }
    };

    handleChangePassword = e =>
    {
        this.setState({
            password: e.target.value.replace(" ", "")
        });
        if(this.state.password.length >= 5)
        {
            this.setState({
                passwordVal: true,
            })
        }
        else
        {
            this.setState({
                passwordVal: false,
            })
        }
    };

    handleChangePasswordConfirm = e =>
    {
        this.setState({
            passwordConfirm: e.target.value.replace(" ", "")
        });
        if(this.state.password === e.target.value)
        {
            this.setState({
                passwordConfirmVal: true,
            })
        }
        else
        {
            this.setState({
                passwordConfirmVal: false,
            })
        }
    };



    handleSubmit = e =>
    {
        e.preventDefault();

        console.log("submit");

        const {name, surname,
            email, emailVal, login, loginVal, password, passwordVal, passwordConfirm, passwordConfirmVal,
            address, city, zipCode, phone} = this.state;

        console.log(this.state.emailVal);
        console.log(this.state.email);
        console.log(this.state.loginVal);
        console.log(this.state.login);
        console.log(this.state.passwordVal);
        console.log(this.state.password);
        console.log(this.state.passwordConfirmVal);
        console.log(this.state.passwordConfirm);

        if(this.state.emailVal && this.state.loginVal && this.state.passwordVal && this.state.passwordConfirmVal)
        {
            console.log("sending");

            const url = "http://localhost:3012/users";

            fetch(url,
                {
                    headers: {"Content-Type": "application/json"},
                    method: 'POST',
                    dataType: "json",
                    body: JSON.stringify({name, surname, email, login, password, address, city, zipCode, phone}),
                })
            .then(resp =>{
                if (!resp.ok) {
                    throw new Error("something is wrong...");
                }
                else
                {
                    this.setState({
                        submitMessage: "Wiadomośc została wysłana. Skontaktujemy się wkrótce.",
                        name: "",
                        email: "",
                        message: "",
                    });
                    console.log(resp);
                }
            })
            .catch(err => console.error(err));
        }
        else
        {
            this.setState({
                submitMessage: "",
                submitHandle: true,
            });
        }
    };


    render() {
        const {name, surname,
            email, emailVal, login, loginVal, password, passwordVal,
            passwordConfirm, address, city, zipCode, phone, submitHandle} = this.state;
        return (
            <>
                <Logo/>
                <section className="registration">
                    <div className="container lar-cont">
                        <div className="registration-content">
                            <form className="registration-content-form"
                                  onSubmit={this.handleSubmit}>
                                <legend>
                                    Rejestracja
                                </legend>
                                <h2>Dane personalne</h2>
                                <section className="registration-content-form-sections">
                                    <label>Imię:
                                        <span className="big-x2">*</span>
                                        <input type="text"
                                               placeholder="Name"
                                               name="name"
                                               value={name}
                                               onChange={this.handleChangeName}/>

                                    </label>
                                    <label>Nazwisko:
                                        <input type="text"
                                               placeholder="Surname"
                                               name="surname"
                                               value={surname}
                                               onChange={this.handleChangeSurname}/>

                                    </label>
                                    <label>E-mail:
                                        <span className="big-x2">*</span>
                                        <input type="text"
                                               placeholder="E-mail"
                                               name="email"
                                               value={email}
                                               onChange={this.handleChangeEmail}/>
                                    </label>
                                    {(!emailVal && submitHandle) ?
                                        <div className="form-error">
                                            Podany email jest nieprawidłowy!
                                        </div> :
                                        <div className="form-error-invisible"/>}
                                </section>
                                <h2>Dane do logowania</h2>
                                <section className="registration-content-form-sections">
                                    <label>Login:
                                        <span className="big-x2">*</span>
                                        <input type="text"
                                               placeholder="Login"
                                               name="login"
                                               value={login}
                                               onChange={this.handleChangeLogin}
                                              />
                                    </label>
                                    <label>Hasło:
                                        <span className="big-x2">*</span>
                                        <input type="password"
                                               placeholder="Password"
                                               name="password"
                                               value={password}
                                               onChange={this.handleChangePassword}/>
                                    </label>
                                    <label>Powtórz hasło:
                                        <span className="big-x2">
                                            *
                                        </span>
                                        <input type="password"
                                               placeholder="Repeat password"
                                               name="passwordConfirm"
                                               value={passwordConfirm}
                                               onChange={this.handleChangePasswordConfirm}/>
                                    </label>
                                </section>
