import React, {Component} from 'react';
import "./../_lar.scss"
import "./_registration.scss"
import Logo from "../../general_components/header/logo/Logo";
import Footer from "../../general_components/footer/Footer";
import {Link} from "react-router-dom";

class Registration extends Component
{

    state =
        {
            name: "",
            surname: "",
            email: "",
            emailVal: false,

            login: "",
            loginVal: false,
            password: "",
            passwordVal: false,
            passwordConfirm: "",
            passwordConfirmVal: false,

            address: "",
            zipCode: "",
            city: "",
            phone: "",

            submitHandle: false,
        };

    handleChangeName = e =>
    {
        this.setState({
            name: e.target.value
        });
    };

    handleChangeSurname = e =>
    {
        this.setState({
            surname: e.target.value
        });
    };


    emailValidate = (email) =>
    {
        const emailValidationCharSet = /^(([^<>()[\]\\.,;:\s@]+(\.[^<>()[\]\\.,;:\s@]+)*)|(.+))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return emailValidationCharSet.test(email);
    };

    handleChangeEmail = e =>
    {
        this.setState({
            email: e.target.value
        });
        if(this.emailValidate(this.state.email))
        {
            this.setState({
                emailVal: true,
            })
        }
        else
        {
            this.setState({
                emailVal: false,
            })
        }
    };

    handleChangeLogin = e =>
    {
        this.setState({
            login: e.target.value.replace(" ", "")
        });
        if(this.state.password.length >= 3)
        {
            this.setState({
                loginVal: true,
            })
        }
        else
        {
            this.setState({
                loginVal: false,
            })
        }
    };

    handleChangePassword = e =>
    {
        this.setState({
            password: e.target.value.replace(" ", "")
        });
        if(this.state.password.length >= 5)
        {
            this.setState({
                passwordVal: true,
            })
        }
        else
        {
            this.setState({
                passwordVal: false,
            })
        }
    };

    handleChangePasswordConfirm = e =>
    {
        this.setState({
            password: e.target.value.replace(" ", "")
        });
        if(this.state.passwordConfirm.length >= 5 && this.state.password.equals(this.state.passwordConfirm))
        {
            this.setState({
                passwordConfirmVal: true,
            })
        }
        else
        {
            this.setState({
                passwordConfirmVal: false,
            })
        }
    };


    render() {
        const {name, surname,
            email, emailVal, login, loginVal, password, passwordVal,
            passwordConfirm, address, city, zipCode, phone, submitHandle} = this.state;
        return (
            <>
                <Logo/>
                <section className="registration">
                    <div className="container lar-cont">
                        <div className="registration-content">
                            <form className="registration-content-form">
                                <legend>
                                    Rejestracja
                                </legend>
                                <h2>Dane personalne</h2>
                                <section className="registration-content-form-sections">
                                    <label>Imię:
                                        <span className="big-x2">*</span>
                                        <input type="text"
                                               placeholder="Name"
                                               name="name"
                                               value={name}
                                               onChange={this.handleChangeName}/>
                                    </label>
                                    <label>Nazwisko:
                                        <input type="text"
                                               placeholder="Surname"
                                               name="surname"
                                               value={surname}
                                               onChange={this.handleChangeSurname}/>

                                    </label>
                                    <label>E-mail:
                                        <span className="big-x2">*</span>
                                        <input type="text"
                                               placeholder="E-mail"
                                               name="email"
                                               value={email}
                                               onChange={this.handleChangeEmail}/>
                                    </label>
                                    {(!emailVal && submitHandle) ?
                                        <div className="form-error">
                                            Podany email jest nieprawidłowy!
                                        </div> :
                                        <div className="form-error-invisible"/>}
                                </section>
                                <h2>Dane do logowania</h2>
                                <section className="registration-content-form-sections">
                                    <label>Login:
                                        <span className="big-x2">*</span>
                                        <input type="text"
                                               placeholder="Login"
                                               name="login"
                                               value={login}
                                               onChange={this.handleChangeLogin}/>
                                    </label>
                                    <label>Hasło:
                                        <span className="big-x2">*</span>
                                        <input type="text"
                                               placeholder="Password"
                                               name="password"
                                               value={password}
                                               onChange={this.handleChangePassword}/>
                                    </label>
                                    <label>Powtórz hasło:
                                        <span className="big-x2">
                                            *
                                        </span>
                                        <input type="text"
                                               placeholder="Repeat password"
                                               name="passwordConfirm"
                                               value={passwordConfirm}
                                               onChange={this.handleChangePasswordConfirm}/>
                                    </label>
                                </section>
                                <h2>Dane kontaktowe</h2>
                                <section className="registration-content-form-sections">
                                    <label>Adres zamieszkania:
                                        <input type="text"
                                               placeholder="Address"
                                               name="address"
                                               value={address}/>
                                    </label>
                                    <label>Miasto:
                                        <input type="text"
                                               placeholder="City"
                                               name="city"
                                               value={city}/>
                                    </label>
                                    <label>Kod pocztowy:
                                        <input type="text"
                                               placeholder="Zip code"
                                               name="zipCode"
                                               value={zipCode}/>
                                    </label>
                                    <label>Telefon:
                                        <input type="text"
                                               placeholder="Phone"
                                               name="phone"
                                               value={phone}/>
                                    </label>
                                </section>
                                <h2>Zgody</h2>
                                <section className="registration-content-form-sections">
                                    <div>
                                        <input type="checkbox"
                                               id="agreement" checked/>
                                        <label className="registration-content-form-sections-left"
                                               htmlFor="agreement">
                                            <p className="big-x2">
                                                *
                                            </p>
                                            <span/>
                                            <p>
                                                Zapoznałem się z regulaminem sklepu internetowego i akceptuję jego treść.
                                            </p>
                                        </label>
                                    </div>
                                    <p>
                                        Pola oznaczone&nbsp;
                                        <span className="big-x1">*</span>
                                        &nbsp;są obowiązkowe
                                    </p>
                                </section>
                                <div className="lar-content-form-btns">
                                    <Link to="/registration">do logowania</Link>
                                    <input type="submit" value="zarejestruj"/>
                                </div>
                            </form>
                        </div>
                    </div>
                </section>
                <Footer/>
            </>
        )
    }
}

export default Registration;
