import React, {Component} from 'react';
import "./_basket_step_3.scss"
import BasketTableProducts from "../basket_table/basket_table_products/BasketTableProducts";
import BasketTableSummary from "../basket_table/basket_table_summary/BasketTableSummary";

class BasketStep3 extends Component
{
    state = {
        products: [],
    };

    componentDidMount() {

        const url = "http://localhost:3000/products";

        fetch(url)
        .then(response => {
            return response.json()
        })
        .then(products =>
        {
            this.setState({
                products: products
            });
        })
        .catch(function(error) {
            console.log(error);
        });
    };

    handleSubmit = (e) =>
    {
        e.preventDefault();

        const {products} = this.state;
        const {setBasketStep, basketStep, basketSetClear, basket} = this.props;
        setBasketStep(basketStep + 1);

        let newProductsList = [...products]
        let error = false;
        let putError = false;

        let changeId = [];

        let i = 0;

        for (; i < basket.length;) {

            for (let j = 0; j < products.length; j++) {

                if(products[j].id === basket[i].product.id)
                {
                    changeId.push(j)
                    newProductsList[j].quantity2 = (+products[j].quantity2 - +basket[i].amount).toString();
                    // let difference = +products[j].quantity2 - +basket[i].amount;  // -> BasketStep1
                    // if(difference < 0)
                    // {
                    //     console.log("Ktoś już kupił produkt z twojego koszyka")
                    //     error = true;
                    // }
                }
            }
            i++;
        }

        console.log(newProductsList);
        let newProductsList2 = [];

        for (let i = 0; i < newProductsList.length; i++)
        {
            if(newProductsList[i] !== "empty")
            {
                newProductsList2.push(newProductsList[i]);
            }
        }
        console.log(newProductsList2);

        if(i >= basket.length && !error)
        {
            const url = "http://localhost:3000/products/";

            for (let j = 1; j < newProductsList2.length; j++)
            {
                for (let k = 0; k < changeId.length; k++)
                {
                    if(changeId[k] === j)
                    {
                        fetch(url + j,
                            {
                                headers: {"Content-Type": "application/json"},
                                method: 'PUT',
                                dataType: "json",
                                body: JSON.stringify(newProductsList2[j]),
                            })
                        .then(resp =>{
                            if (!resp.ok) {
                                putError = true;
                                throw new Error("something is wrong...");
                            }
                            else
                            {
                                console.log(resp)
                            }
                        })
                        .catch(err => console.error(err));
                    }
                }
            }
            if(!putError)
            {
                setBasketStep(basketStep + 1);
                this.props.basketSetClear();
            }
        }

    };

    render() {
        const {basket, basketSum, currentDelivery, totalSum, priceDisplay, basketStep, handleGoBack} = this.props;
        return (
            <>
                <section className="basket-step-3">
                    <div className="container basket-container">
                        <BasketTableProducts
                            basketStep={basketStep}
                            basket={basket}
                            priceDisplay={priceDisplay}/>
                        <BasketTableSummary
                            basketSum={basketSum}
                            currentDelivery={currentDelivery}
                            totalSum={totalSum}
                            priceDisplay={priceDisplay}/>
                        <section className="basket-step-btns">
                            <button className="btn" onClick={handleGoBack}>
                                Wstecz
                            </button>
                            <button className="btn confirm-btn" onClick={this.handleSubmit} >
                                Potwierdzam
                            </button>
                        </section>
                    </div>
                </section>
            </>
        )
    }
}

export default BasketStep3;
