import React, {Component} from 'react';
import "./_basket_step_3.scss"
import BasketTableProducts from "../basket_table/basket_table_products/BasketTableProducts";
import BasketTableSummary from "../basket_table/basket_table_summary/BasketTableSummary";

class BasketStep3 extends Component
{
    state = {
        products: [],
    };

    componentDidMount() {

        const url = "http://localhost:3000/products";

        fetch(url)
        .then(response => {
            return response.json()
        })
        .then(products =>
        {
            this.setState({
                products: products
            });
        })
        .catch(function(error) {
            console.log(error);
        });
    };

    handleSubmit = (e) =>
    {
        e.preventDefault();

        const {products} = this.state;
        const {setBasketStep, basketStep, basketSetClear, basket} = this.props;
        setBasketStep(basketStep + 1);

        let newProductsList = products;

        for (let i = 0; i < basket.length; i++) {

            for (let j = 0; j < products.length; j++) {
                if(products[j].id === basket[i].id)
                {
                    console.log(newProductsList[j]);
                    +newProductsList[j].quality - +basket[i].quality;
                    let difference = +newProductsList[j].quality - +basket[i].quality;
                    console.log(newProductsList[j]);
                    if(difference < 0)
                    {
                        console.log("Ktoś już kupił produkt z twojego koszyka")
                    }
                }

            }


        }



        basketSetClear();

    };

    render() {
        const {basket, basketSum, currentDelivery, totalSum, priceDisplay, basketStep, handleGoBack} = this.props;
        return (
            <>
                <section className="basket-step-3">
                    <div className="container basket-container">
                        <BasketTableProducts
                            basketStep={basketStep}
                            basket={basket}
                            priceDisplay={priceDisplay}/>
                        <BasketTableSummary
                            basketSum={basketSum}
                            currentDelivery={currentDelivery}
                            totalSum={totalSum}
                            priceDisplay={priceDisplay}/>
                        <section className="basket-step-btns">
                            <button className="btn" onClick={handleGoBack}>
                                Wstecz
                            </button>
                            <button className="btn confirm-btn" onClick={this.handleSubmit} >
                                Potwierdzam
                            </button>
                        </section>
                    </div>
                </section>
            </>
        )
    }
}

export default BasketStep3;
