                    <div className="container">
                        <div className="products-content">
                            <ul>
                                {products.map( element =>
                                    <li key={element.id}>
                                        {element.id}
                                    </li>
                                )}
                            </ul>
                        </div>
                    </div>
                </section>
            </>
        )
    }
}

export default Products;
