import React, {Component} from 'react';
import "./_contact.scss"
import Navigation from "../general_components/header/navigation/Navigation";
import LogRegister from "../general_components/header/log_register/LogRegister";
import Footer from "../general_components/footer/Footer";

class Contact extends Component
{
    state =
        {
            name: "",
            nameVal: false,
            email: "",
            emailVal: false,
            message: "",
            messageVal: false,
            submitMessage: "",
            errorClassName: "form-error",
            submitHandle: false,
        };

    emailValidate = (email) =>
    {
        const emailValidationCharSet = /^(([^<>()[\]\\.,;:\s@]+(\.[^<>()[\]\\.,;:\s@]+)*)|(.+))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return emailValidationCharSet.test(email);
    };

    handleChangeName = e =>
    {
        this.setState({
            name: e.target.value.replace(" ", ""),
        });
        if(this.state.name !== "")
        {
            this.setState({
                nameVal: true,
            })
        }
    };

    handleChangeEmail = e =>
    {
        this.setState({
            email: e.target.value
        });
        if(this.emailValidate(this.state.email))
        {
            this.setState({
                emailVal: true,
            })
        }
    };

    handleChangeMessage = e =>
    {
        this.setState({
            message: e.target.value,
        });
        if(this.state.message.length >= 119)
        {
            this.setState({
                messageVal: true,
            })
        }
    };

    handleSubmit = e =>
    {
        e.preventDefault();

        const {name, email, message} = this.state;

        let nameValVar;
        let emailValVar;
        let messageValVar;

        if(name !== "")
        {
            nameValVar = true;
            this.setState({
                nameVal: true,
            })
        }

        if(this.emailValidate(email))
        {
            emailValVar = true;
            this.setState({
                emailVal: true,
            })
        }

        if(message.length > 120)
        {
            messageValVar = true;
            this.setState({
                messageVal: true,
            })
        }

        if(nameValVar && emailValVar && messageValVar)
        {

            const url = "http://localhost:3000/messages";

            fetch(url,
                {
                    headers: {"Content-Type": "application/json"},
                    method: 'POST',
                    dataType: "json",
                    body: JSON.stringify({name, email, message}),
                })
            .then(resp =>{
                if (!resp.ok) {
                    throw new Error("something is wrong...");
                }
                else
                {
                    this.setState({
                        submitMessage: "Wiadomośc została wysłana! Skontaktujemy się wkrótce.",
                        name: "",
                        email: "",
                        message: "",
                    });
                    console.log(resp);
                }
            })
            .catch(err => console.error(err));
        }
        else
        {
            this.setState({
                submitMessage: "",
                submitHandle: true,
            });
        }
    };




    render() {
        return (
            <>
                <LogRegister/>
                <Navigation/>
                <section className="contact">
                    <div className="container">
                        <header className="contact-header">
                            <section className="contact-header-logo">

                            </section>
                            <section className="contact-header-title">
                                <p>
                                    Copper Shop
                                </p>
                                <p>
                                    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                    Lorem Ipsum
                                </p>
                            </section>
                        </header>
                        <section className="contact-content">
                            <section className="contact-content-address">
                                <div className="contact-content-address-img"/>
                                <div className="contact-content-address-data">
                                    <h3>
                                        Lorem
                                    </h3>
                                    <h3>
                                        <i className="fas fa-lg fa-home"/>
                                        ul. Lorem ipsum 6
                                    </h3>
                                    <h3>
                                        <i className="fas fa-lg fa-phone-alt"/>
                                        +48 342 549 320
                                    </h3>
                                    <h3>
                                        <i className="fas fa-lg fa-envelope"/>
                                        coppershop@shop.pl
                                    </h3>

                                </div>
                            </section>
                            <form className="contact-content-form">
                                <legend>
                                    Skontaktuj się z nami
                                </legend>
                                <section className="contact-content-form-section">
                                    <fieldset>
                                        <label form="contact-name">Twoje imię:</label>
                                        <input type="text"
                                               placeholder="Your name"
                                               id="contact-name"
                                               name="name"
                                               value={name}/>
                                        <label form="contact-email">E-mail:</label>
                                        <input type="text"
                                               placeholder="E-mail"
                                               id="contact-email"
                                               name="email"
                                               value={email}/>
                                    </fieldset>
                                    <fieldset>
                                        <label form="contact-message">Twoja wiadomość:</label>
                                        <textarea placeholder="Your message"
                                                  id="message"
                                                  name="message"
                                                  value={message}/>
                                    </fieldset>
                                </section>
                                <input className="form-btn" type="submit" value="Wyślij wiadomość"/>
                            </form>
                        </section>
                    </div>
                </section>



                <Footer/>
            </>
        )
    }
}

export default Contact;
