import React, {Component} from 'react';
import "./../../basket/basket_table/basket_table_products/_basket_table_products.scss"
import "./_user_panel_order_view.scss"
import BasketTableProducts from "../../basket/basket_table/basket_table_products/BasketTableProducts";

class UserPanelOrderView extends Component
{
    state =
        {
            user: "",
            orders: []
        }
    componentDidMount()
    {
        const urlUsers = "http://localhost:3012/users";

        fetch(urlUsers)
        .then(response => {
            return response.json()
        })
        .then(users =>
        {
            for (let i = 0; i < users.length; i++)
            {
                if(users[i].login === this.props.login)
                {
                    this.setState({
                        user: users[i]
                    });
                }
            }
        })
        .catch(function(error) {
            console.log(error);
        });

        const urlOrders = "http://localhost:3014/orders";

        fetch(urlOrders)
        .then(response => {
            return response.json()
        })
        .then(orders =>
        {
            let userOrders = [];
            for (let i = 0; i < orders.length; i++)
            {
                if(orders[i].login === this.props.login)
                {
                    userOrders.push(orders[i]);
                }
            }
            this.setState({
                orders: userOrders
            });
        })
        .catch(function(error) {
            console.log(error);
        });
    };

    render() {
        const {user, orders} = this.state
        const {priceDisplay, currentOrderView} = this.props
        return (
            <>
                <section className="basket-table-products">
                    <table className="basket-table-products-content">
                        <thead>
                            <tr>
                                <th className="product-col-s">Lp.</th>
                                <th className="product-col-m">miniaturka</th>
                                <th className="product-col-l">nazwa produktu</th>
                                <th className="product-col-m">kod</th>}
                                <th className="product-col-s">ilość</th>
                                <th className="product-col-m">cena</th>
                                <th className="product-col-m">suma</th>
                            </tr>
                        </thead>
                        <tbody>
                            {orders[currentOrderView].map((element, index)=>
                                <tr key={"basket-" + index}>
                                    <td className="product-col-s basket-product-id">
                                        {index + 1}.
                                    </td>
                                    <td className="product-col-m">
                                        <img src={"./assets/products/" + element.product.id + ".jpeg"}
                                             alt={"product_" + element.product.id}
                                             width="100%"/>
                                    </td>
                                    <td className="product-col-l basket-product-name">
                                        bransoletka <span>{element.product.id}</span>
                                    </td>
                                    {basketStep === 3 &&
                                    <td className="product-col-m basket-product-code">
                                        {element.product.code}
                                    </td>
                                    }
                                    <td className="product-col-s basket-product-amount">
                                        {element.amount}
                                    </td>
                                    <td className="product-col-m basket-product-price">
                                        {priceDisplay(element.product.price)}
                                    </td>
                                    <td className="product-col-m basket-product-sum">
                                        {priceDisplay(element.total)}
                                    </td>
                                </tr>)}
                        </tbody>
                    </table>
                </section>
            </>
        )
    }
}

export default UserPanelOrderView;
