import React, {Component} from 'react';
import "./../../basket/basket_table/basket_table_products/_basket_table_products.scss"
import "./_user_panel_order_view.scss"
import {Link} from "react-router-dom";

class UserPanelOrderView extends Component
{
    state = {
        order: ""
    }

    componentDidMount() {

            const urlOrders = "http://localhost:3014/orders";

            fetch(urlOrders)
            .then(response => {
                return response.json()
            })
            .then(orders =>
            {
                let userOrders = [];

                for (let i = 0; i < orders.length;i++)
                {
                    if(orders[i].login === this.props.login)
                    {
                        userOrders.push(orders[i]);
                    }
                }

                console.log(userOrders)

                for (let i = 0; i < userOrders.length; i++)
                {
                    if(+userOrders[i].id === +this.props.currentOrderView)
                    {
                        this.setState({
                            order: [...userOrders[i].basket]
                        });
                        console.log([...userOrders[i].basket])
                        break;
                    }
                }

            })
            .catch(function(error) {
                console.log(error);
            });

    };

    handleGoBack = () =>
    {

    }

    render() {
        const {order} = this.state
        const {priceDisplay} = this.props
        return (
            <>
                <section className="upov">
                    <table className="upov-content">
                        <thead>
                            <tr>
                                <th className="product-col-s">Lp.</th>
                                <th className="product-col-l">nazwa produktu</th>
                                <th className="product-col-m">kod</th>
                                <th className="product-col-s">ilość</th>
                                <th className="product-col-m">cena</th>
                                <th className="product-col-m">suma</th>
                            </tr>
                        </thead>
                        <tbody>
                        {order &&
                            order.map((element, index)=>
                                <tr key={"basket-" + index}>
                                    <td className="product-col-s basket-product-id">
                                        {index + 1}.
                                    </td>
                                    <td className="product-col-l basket-product-name">
                                        bransoletka <span>{element.product.id}</span>
                                    </td>
                                    <td className="product-col-m basket-product-code">
                                        {element.product.code}
                                    </td>
                                    <td className="product-col-s basket-product-amount">
                                        {element.amount}
                                    </td>
                                    <td className="product-col-m basket-product-price">
                                        {priceDisplay(element.product.price)}
                                    </td>
                                    <td className="product-col-m basket-product-sum">
                                        {priceDisplay(element.total)}
                                    </td>
                                </tr>)}
                        </tbody>
                    </table>
                    <section className="basket-step-btns">
                        <button className="btn" onClick={handleGoBack}>
                            Wstecz
                        </button>
                    </section>
                </section>
            </>
        )
    }
}

export default UserPanelOrderView;
