import React, {Component} from 'react';
import "./_basket_step_2.scss"

class BasketStep2 extends Component
{
    state =
        {
            disabled: true,

            users: "",

            name: "",
            nameVal: false,
            surname: "",
            surnameVal: false,
            email: "",
            emailVal: false,

            address: "",
            addressVal: false,
            zipCode: "",
            zipCodeVal: false,
            city: "",
            cityVal: false,
            phone: "",
            phoneVal: false,

            agreementChecked: false,

            submitHandle: false,
            submitMessage: ""
        };

    componentDidMount()
    {
        const url = "http://localhost:3012/users";

        fetch(url)
        .then(response => {
            return response.json()
        })
        .then(users =>
        {
            this.setState({
                users: users
            });
        })
        .catch(function(error) {
            console.log(error);
        });
    };

    validation = (e, valType, length) =>
    {
        e.target.value.length >= length ? valType=true : valType=false;
        return valType;
    };

    isDisabled = () =>
    {
        const {nameVal, surnameVal, emailVal, addressVal, zipCodeVal, cityVal, phoneVal, agreementChecked} = this.state;
        if((nameVal && surnameVal && emailVal && addressVal && zipCodeVal && cityVal && phoneVal && agreementChecked)
            || this.props.deliveryDetailsVal)
        {
            this.setState({
                disabled: false
            });
        }
        else
        {
            this.setState({
                disabled: true
            });
        }
    };

    handleChangeName = e =>
    {
        let nameVal;
        this.setState({
            name: e.target.value,
            nameVal: this.validation(e, nameVal, 2)
        });
        this.isDisabled();
    };

    handleChangeSurname = e =>
    {
        let surnameVal;
        this.setState({
            surname: e.target.value,
            surnameVal: this.validation(e, surnameVal, 2)
        });
        this.isDisabled();
    };

    handleChangeAddress = e =>
    {
        let addressVal;
        this.setState({
            address: e.target.value,
            addressVal: this.validation(e, addressVal, 4)
        });
        this.isDisabled();
    };

    handleChangeCity = e =>
    {
        let cityVal;
        this.setState({
            city: e.target.value,
            cityVal: this.validation(e, cityVal, 2)
        });
        this.isDisabled();
    };

    handleChangeZipCode = e =>
    {
        let zipCodeVal;
        e.target.value.length === 6 ? zipCodeVal=true : zipCodeVal=false;
        this.setState({
            zipCode: e.target.value,
            zipCodeVal: zipCodeVal
        });
        this.isDisabled();
    };

    handleChangePhone = e =>
    {
        let phoneVal;
        this.setState({
            phone: e.target.value,
            phoneVal: this.validation(e, phoneVal, 9)
        });
        this.isDisabled();
    };

    emailValidate = (email) =>
    {
        const emailValidationCharSet = /^(([^<>()[\]\\.,;:\s@]+(\.[^<>()[\]\\.,;:\s@]+)*)|(.+))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return emailValidationCharSet.test(email);
    };

    handleChangeEmail = e =>
    {
        this.setState({
            email: e.target.value
        });

        this.emailValidate(e.target.value) ?
            this.setState({
                emailVal: true,
            }) :
            this.setState({
                emailVal: false,
            });
        this.isDisabled();
    };

    handleChangeAgreement = (e) =>
    {
        if(e.currentTarget.checked === true)
        {
            this.setState({
                agreementChecked: true
            });
            this.isDisabled();
        }
        else
        {
            this.setState({
                agreementChecked: false
            });
            this.isDisabled();
        }


    };

    // handleClickAgreement = () =>
    // {
    //     this.isDisabled();
    // };

    handleSubmit = () =>
    {
        if(this.state.disabled === false)
        {
            const {setBasketStep, basketStep} = this.props;
            setBasketStep(basketStep + 1);

            const {name, surname, email, address, zipCode, city, phone} = this.state;
            this.props.changeDeliveryDetails(name, surname, email, address, zipCode, city, phone);

            console.log("submit");
        }

    };

    handleGoBack = () =>
    {
        const {setBasketStep, basketStep} = this.props;
        setBasketStep(basketStep - 1)
    };

    render() {

        const {disabled, email, emailVal, name, nameVal, surname, surnameVal, address, addressVal,
            city, cityVal, zipCode, zipCodeVal, phone, phoneVal, submitHandle, loginAvailable, emailAvailable, agreementChecked, submitMessage} = this.state;
        return (
            <>
                <section className="basket-step-2">
                    <div className="container basket-container">
                        <form>
                            <legend>Dane do wysyłki</legend>
                            <section className="basket-step-2-form-section">
                                <label>Imię:
                                    <span className="big-x2">*</span>
                                    <input type="text"
                                           placeholder="Name"
                                           name="name"
                                           value={name}
                                           onChange={this.handleChangeName}/>
                                </label>
                                <label>Nazwisko:
                                    <span className="big-x2">*</span>
                                    <input type="text"
                                           placeholder="Surname"
                                           name="surname"
                                           value={surname}
                                           onChange={this.handleChangeSurname}/>
                                </label>
                                <label className="obligatory">E-mail:
                                    <span className="big-x2">*</span>
                                    <input type="text"
                                           placeholder="E-mail"
                                           name="email"
                                           value={email}
                                           onChange={this.handleChangeEmail}/>
                                </label>
                                <label>Adres zamieszkania:
                                    <span className="big-x2">*</span>
                                    <input type="text"
                                           placeholder="Address"
                                           name="address"
                                           value={address}
                                           onChange={this.handleChangeAddress}/>
                                </label>
                                <label>Miasto:
                                    <span className="big-x2">*</span>
                                    <input type="text"
                                           placeholder="City"
                                           name="city"
                                           value={city}
                                           onChange={this.handleChangeCity}/>
                                </label>
                                <label>Kod pocztowy:
                                    <span className="big-x2">*</span>
                                    <input type="text"
                                           placeholder="Zip code"
                                           name="zipCode"
                                           value={zipCode}
                                           onChange={this.handleChangeZipCode}/>
                                </label>
                                <label>Telefon:
                                    <span className="big-x2">*</span>
                                    <input type="text"
                                           placeholder="Phone"
                                           name="phone"
                                           value={phone}
                                           onChange={this.handleChangePhone}/>
                                </label>
                            </section>
                            <section className="basket-step-2-form-section">
                                <div className="basket-step-2-form-section-agreements">
                                    <input type="checkbox"
                                           id="agreement"
                                           onChange={this.handleChangeAgreement}/>
                                    <label htmlFor="agreement">
                                        <p>*</p>
                                        <span/>
                                        <p>
                                            Akceptuję regulamin sklepu
                                        </p>
                                    </label>
                                </div>
                                {/*{(!agreementChecked && submitHandle) ?*/}
                                {/*    <div className="form-error">*/}
                                {/*        Zgoda jest obowiązkowa*/}
                                {/*    </div>:*/}
                                {/*    <div className="form-error-invisible"/>}*/}
                                <p className="basket-step-2-form-section-info">
                                    Pola oznaczone&nbsp;
                                    <span className="big-x1">*</span>
                                    &nbsp;są obowiązkowe
                                </p>
                            </section>
                        </form>
                        <section className="basket-step-btns">
                            {/*{!currentDelivery && <p>Aby przejść dalej wybierz opcje dostawy</p>}*/}
                            <button onClick={this.handleGoBack}>
                                Wstecz
                            </button>
                            <button onClick={this.handleSubmit}>
                                Dalej
                            </button>
                        </section>
                    </div>
                </section>
            </>
        )
    }
}

export default BasketStep2;
