import React, {Component} from 'react';
import "./_user_panel.scss"
import {Link, Route} from "react-router-dom";
import Header from "../../general_components/header/Header";
import Products from "../products/Products";
import Info from "../info/Info";
import Footer from "../../general_components/footer/Footer";
import Home from "../../home/Home";

class UserPanel extends Component
{

    render() {
        const {login, setClearLogin, path, basketAmount, basketSum} = this.props
        return (
            <>
                <Header login={login}
                        setClearLogin={setClearLogin}
                        path={path}
                        basketSum={basketSum}
                        basketAmount={basketAmount}/>
                <Footer/>
                <section className="user-panel">
                    <div className="container">
                        <nav className="user-panel-nav">
                            <ul className="main-nav-menu">
                                <li>
                                    <Link to="/user-panel/data">Twoje dane</Link>
                                </li>
                                <li>
                                    <Link to="/user-panel/orders">Zam√≥wienia</Link>
                                </li>
                            </ul>
                            {login && <h4 onClick={()=>setClearLogin()}>Wyloguj</h4>}
                        </nav>
                        <section className="user-panel-content">

                        </section>
                    </div>
                </section>
                <Route exact path='/user-panel/orders' render={() => <Orders path="/user-panel/orders"/>}/>
            </>
        )
    }
}

export default UserPanel;
