import React, {Component} from 'react';
import "./_user_panel_orders.scss"

class UserPanelOrders extends Component
{
    state =
        {
            user: ""
        }
    componentDidMount() {

        const url = "http://localhost:3012/users";

        fetch(url)
        .then(response => {
            return response.json()
        })
        .then(users =>
        {
            for (let i = 0; i < users.length; i++)
            {
                if(users[i].login === this.props.login)
                {
                    this.setState({
                        user: users[i]
                    });
                }
            }


        })
        .catch(function(error) {
            console.log(error);
        });
    };


    render() {
        const {user} = this.state
        return (
            <>
                <h1>
                    ORDERS
                </h1>

                <section className="upo">
                    <table className="upo-table">
                        <thead>
                        <tr>
                            <th className="product-col-s">Lp.</th>
                            <th className="product-col-l">kod zamówienia</th>
                            <th className="product-col-m">data</th>
                            <th className="product-col-s">kwota</th>
                        </tr>
                        </thead>
                        <tbody>
                        {user.orders.map((element, index)=>
                            <tr key={"upo-" + index}>
                                <td className="product-col-s upo-id">
                                    {index + 1}.
                                </td>
                                <td className="product-col-m basket-product-code">
                                    {element.product.code}
                                </td>
                                <td className="product-col-s basket-product-amount">
                                    {basketStep === 1 &&
                                    <input type="number" value={element.amount}
                                           max={this.maxValue(element.product.id)} min="0"
                                           onChange={(e) => basketOnAmountChange(e.currentTarget)}/>}
                                    {basketStep === 3 && element.amount}
                                </td>
                                <td className="product-col-m basket-product-price">
                                    {priceDisplay(element.product.price)}
                                </td>
                                <td className="product-col-m basket-product-sum">
                                    {priceDisplay(element.total)}
                                </td>
                                {basketStep === 1 &&
                                <td className="product-col-s basket-product-bin">
                                    <i className="fas fa-trash-alt"
                                       onClick={(e) => basketDelete(e.currentTarget)}/>
                                </td>}
                            </tr>)}
                        </tbody>
                    </table>

                </section>

            </>
        )
    }
}

export default UserPanelOrders;
